import styled from 'styled-components';

export const Container = styled.div`


`

export const Content = styled.div`
    display: flex;
    align-items: center;
    justify-content: flex-start;

    a{
        padding-left: 0.5rem;
        font-size: 1rem;
        color: var(--white-2);
        text-decoration: none;

        transition: filter 0.2s;

        &:hover{
            filter: brightness(1.2);
        }
    }

`

